import React, { useState } from 'react';
import Card from './Card'; // Make sure to import your Card component

const cardsData = [
  // Your card data
];

const ScrollableCardList = () => {
  const [startIndex, setStartIndex] = useState(0);

  const handleScroll = (direction) => {
    const newIndex = direction === 'left' ? startIndex - 1 : startIndex + 1;
    if (newIndex >= 0 && newIndex <= cardsData.length - 5) {
      setStartIndex(newIndex);

      // Calculate the new scroll position
      const scrollAmount = newIndex * 220; // Adjust this value based on card width
      document.querySelector('.card-container').style.transform = `translateX(-${scrollAmount}px)`;
    }
  };

  const visibleCards = cardsData.slice(startIndex, startIndex + 5);

  return (
    <div className="scrollable-card-list">
      <div className="card-container">
        {visibleCards.map((card) => (
          <Card key={card.id} title={card.title} imageSrc={card.imageSrc} className="card" />
        ))}
        {/* Add empty card placeholders to fill the remaining space */}
        {visibleCards.length < 5 && (
          <div className="empty-card-placeholder" style={{ flex: `0 0 ${(5 - visibleCards.length) * 220}px` }}></div>
        )}
      </div>
      <div className="arrow-buttons">
        <button onClick={() => handleScroll('left')}>Left</button>
        <button onClick={() => handleScroll('right')}>Right</button>
      </div>
    </div>
  );
};

export default ScrollableCardList;
