import React, { useState } from 'react';
import Card from './Card';
import './ScrollableList.css'; // Create this CSS file for custom styling

const ScrollableCardList = ({ cards }) => {
  const [scrollPosition, setScrollPosition] = useState(0);

  const handleScroll = (scrollAmount) => {
    setScrollPosition(scrollPosition + scrollAmount);
  };

  return (
    <div className="scrollable-list">
      <button
        className="scroll-button left"
        onClick={() => handleScroll(-1)}
        disabled={scrollPosition === 0}
      >
        &lt;
      </button>
      <div className="cards-container">
        {cards.slice(scrollPosition, scrollPosition + 5).map((card, index) => (
          <Card key={index} imageSrc={card.imageSrc} title={card.title} />
        ))}
      </div>
      <button
        className="scroll-button right"
        onClick={() => handleScroll(1)}
        disabled={scrollPosition >= cards.length - 5}
      >
        &gt;
      </button>
    </div>
  );
};

export default ScrollableCardList;
