import React, { useState } from 'react';
import Card from './Card';


const cardsData = [
  // Your card data here


  { id: 1, title: 'Card 1', imageSrc: 'image1.jpg' },
  { id: 2, title: 'Card 2', imageSrc: 'image2.jpg' },
  { id: 1, title: 'Card 1', imageSrc: 'image1.jpg' },
  { id: 2, title: 'Card 2', imageSrc: 'image2.jpg' },
  { id: 1, title: 'Card 1', imageSrc: 'image1.jpg' },
  { id: 2, title: 'Card 2', imageSrc: 'image2.jpg' },
  { id: 1, title: 'Card 1', imageSrc: 'image1.jpg' },
  { id: 2, title: 'Card 2', imageSrc: 'image2.jpg' },
  { id: 1, title: 'Card 1', imageSrc: 'image1.jpg' },
  { id: 2, title: 'Card 2', imageSrc: 'image2.jpg' },
  { id: 1, title: 'Card 1', imageSrc: 'image1.jpg' },
  { id: 2, title: 'Card 2', imageSrc: 'image2.jpg' },
  { id: 1, title: 'Card 1', imageSrc: 'image1.jpg' },
  { id: 2, title: 'Card 2', imageSrc: 'image2.jpg' },
  { id: 1, title: 'Card 1', imageSrc: 'image1.jpg' },
  { id: 2, title: 'Card 2', imageSrc: 'image2.jpg' },
];

const ScrollableCardList = () => {
  const [startIndex, setStartIndex] = useState(0);

  const handleScroll = (direction) => {
    if (direction === 'left') {
      setStartIndex((prevIndex) => Math.max(prevIndex - 1, 0));
    } else {
      setStartIndex((prevIndex) => Math.min(prevIndex + 1, cardsData.length - 5));
    }
  };

  const visibleCards = cardsData.slice(startIndex, startIndex + 5);

  return (
    <div className="scrollable-card-list">
      <div className="card-container">
        {visibleCards.map((card) => (
          <Card key={card.id} title={card.title} imageSrc={card.imageSrc} />
        ))}
      </div>
      <div className="arrow-buttons">
        <button onClick={() => handleScroll('left')} disabled={startIndex === 0}>
          Left
        </button>
        <button onClick={() => handleScroll('right')} disabled={startIndex === cardsData.length - 5}>
          Right
        </button>
      </div>
    </div>
  );
};

export default ScrollableCardList;
