import React, { useState } from 'react';
import './App.css'; // Your CSS file for styling

const cardData = [
  { id: 1, imageUrl: 'image1.jpg' },
  { id: 2, imageUrl: 'image2.jpg' },
  // ... more card data
];

const Card = ({ imageUrl }) => (
  <div className="card">
    <img src={imageUrl} alt="Card" />
  </div>
);

const ScrollableCardList = () => {
  const [scrollPosition, setScrollPosition] = useState(0);

  const handleScroll = (scrollOffset) => {
    setScrollPosition(scrollPosition + scrollOffset);
  };

  const scrollContainerStyle = {
    transform: `translateX(-${scrollPosition}px)`,
  };

  return (
    <div className="app">
      <div className="scroll-container" style={scrollContainerStyle}>
        {cardData.map((card) => (
          <Card key={card.id} imageUrl={card.imageUrl} />
        ))}
      </div>
      <button
        className="nav-button left"
        onClick={() => handleScroll(-200)} // Adjust the scroll offset
        disabled={scrollPosition === 0}
      >
        &lt; Left
      </button>
      <button
        className="nav-button right"
        onClick={() => handleScroll(200)} // Adjust the scroll offset
        // Disable the button when no more cards to show
        disabled={scrollPosition >= (cardData.length - 1) * 200} // Adjust the scroll offset
      >
        Right &gt;
      </button>
    </div>
  );
};

export default ScrollableCardList;
